    const newUser = new User(req.body);
    newUser.userId = newUser.mobileNumber;
    newUser.status = "Pending";

    newUser.save((err, result) => {
        if (err){
            //server error
            res.status(500);
            res.send(err);
        }
        else if(result){
            console.log("New user added for approval: " + result);
            res.status(200).send(result);
        }
    });
};

