};

// DELETE (actually using PATCH)
export const deleteUser = (req, res) => {

    console.log(req.params.userId);
    User.updateOne(
        {userId: req.params.userId},
        {"status": "deleted"},
        (err, result) => {
            if (err){
                //server error
                res.status(401);
                res.send(err);
            }
            else {
                console.log("Updated status to deleted for userId: ", req.params.userId);
